package com.rfl.trn.starr_cell;import com.google.firebase.database.FirebaseDatabase;import com.google.firebase.messaging.FirebaseMessaging;public class OfflineCapabilities extends android.app.Application {    @Override    public void onCreate() {        super.onCreate();        FirebaseDatabase.getInstance().setPersistenceEnabled(true);        FirebaseMessaging.getInstance().setAutoInitEnabled(true);        FirebaseMessaging.getInstance().subscribeToTopic("StarCell");    }}